# Application Problem 4 -- Integration# Adding the datax = [0; 0.375; 0.75; 1.5; 2.25; 3; 4.5; 6; 7.5; 9; 12; 15; 18; 21; 24; 27; 28.5; 30];f = [0; 0.801; 1.083; 1.473; 1.74; 1.929; 2.157; 2.25; 2.28; 2.265; 2.142; 1.923; 1.641; 1.308; 0.924; 0.504; 0.276; 0];g = [0; -.369; -.513; -.678; -.783; -.876; -1.05; -1.191; -1.284; -1.338; -1.344; -1.251; -1.101; -.9; -.648; -.369; -.21; 0];plot(x,f)hold onplot(x,g)hold off# Total Area = area of f - area of g# Reselect points so x is evenly spaced to give a nice hadjusted = [1 6 8 10:16 18];x = x(adjusted);f = f(adjusted);g = g(adjusted);plot(x,f)hold onplot(x,g)hold off# Use Composite Simpson's Rule# Because h = 3, no constant to multiply totals byf_area = 0;g_area = 0;for i = 1:length(x)  if i == 1 || i == length(x)    f_area = f_area + f(i);    g_area = g_area + g(i);  endif  if mod(i,2) == 0 && i != 1 && i != length(x)    f_area = f_area + 4*f(i);    g_area = g_area + 4*g(i);  endif  if mod(i,2) == 1 && i != 1 && i != length(x)    f_area = f_area + 2*f(i);    g_area = g_area + 2*g(i);  endifendfortotal_area = f_area - g_area;