# Application Plus One# g(np) derived from regression modelsfunction f_x = f(x)  f_x = -1.*8.234.*x + 5.847.*x.^2 - 72.321;endfunction# Secant Method Algorithmfunction answer = secant(p_0,p_1,TOL,n_0)    for i = 2:n_0    # initalize q_i    q_0 = f(p_0);    q_1 = f(p_1);        # next approximation    p_2 = p_1 - (q_1 * (p_1 - p_0) / (q_1 - q_0));        # check if approximation is within tolerance    if abs(p_2 - p_1) <= TOL      answer = p_2;      display(i)      break    endif        # Message failure    if i == n_0      display("Method Failed")    endif            # Calculating the error    if i > 1      if i > 2        old_error = error;      endif      error = abs(p_1 - p_0);      if i > 2        error_ratio = error/(old_error^1.618);        #display(error_ratio)      endif    endif        # reset p_i    p_0 = p_1;    p_1 = p_2;      endfor    endfunction# Graphing the function to find p_0 and p_1a = 0;b = 30;x = a:.1:b;plot(x,f(x));hold onplot(x,0);hold off# Using the Secant Method to find the root## Root between 4 and 5## Root represents the point at which Cyanobacteria will dominatedominance_point = secant(4,4.1,10^(-5),10^2)